%pyspark

def normalize_line(line):
  try:
    title, year, genres = re.split(r'(\d+),\"?(.+)\((\d+)\)[ ]*\"?,(.+)', line)[2:5]

    title = title.strip()
    year = None if len(year) < 2 else int(year)
    genres = [genre.strip() for genre in genres.split('|')]

    return (title, year, genres)
  except:
    return (None, None, None)

