%spark.ipyspark

# support functions

import re


def normalize_movies(line):
  try:
    movieId, title, year, genres = re.split(r"(\d+),\"?(.+)\((\d+)\)[ ]*\"?,(.+)", line)[1:5]
    movieId = int(movieId)
    title = title.strip()
    year = None if len(year) < 2 else int(year)
    genres = [genre.strip() for genre in genres.split('|')]
    return (movieId, title, year, genres)
  except:
    return (None, None, None, None)


def normalize_ratings(line):
  try:
    movieId, rating = re.split(r"(\d+),(\d+),(\d{1}.\d{1}),(\d+)", line)[2:4]
    movieId = int(movieId)
    rating = float(rating)
    return (movieId, rating)
  except:
    return (None, None)


def to_csv(rdd):
  return ','.join(str(line) for line in rdd)

